mapper.select:
SELECT * FROM EqmRepairRecord 
WHERE keyID>=(select keyID from EqmRepairRecord  
WHERE 1=1

    AND keyID = #{keyID}


    AND propertyNo = #{propertyNo}


    AND eqmName = #{eqmName}


    AND adminId = #{adminId}


    AND adminName = #{adminName}


    AND applyId = #{applyId}


    AND applyName = #{applyName}


    AND applyLevel = #{applyLevel}


    AND applyDate = #{applyDate}


    AND repairReason = #{repairReason}


    AND handleStatus = #{handleStatus}


    AND handleStatusName = #{handleStatusName}


    AND handleDate = #{handleDate}


    AND handleReason = #{handleReason}

ORDER BY KeyID ASC
limit #{pageCurrent},1 )        

    AND keyID = #{keyID}


    AND propertyNo = #{propertyNo}


    AND eqmName = #{eqmName}


    AND adminId = #{adminId}


    AND adminName = #{adminName}


    AND applyId = #{applyId}


    AND applyName = #{applyName}


    AND applyLevel = #{applyLevel}


    AND applyDate = #{applyDate}


    AND repairReason = #{repairReason}


    AND handleStatus = #{handleStatus}


    AND handleStatusName = #{handleStatusName}


    AND handleDate = #{handleDate}


    AND handleReason = #{handleReason}

limit #{pageSize}

select count(*) from EqmRepairRecord WHERE 1=1 

    AND keyID = #{keyID}


    AND propertyNo = #{propertyNo}


    AND eqmName = #{eqmName}


    AND adminId = #{adminId}


    AND adminName = #{adminName}


    AND applyId = #{applyId}


    AND applyName = #{applyName}


    AND applyLevel = #{applyLevel}


    AND applyDate = #{applyDate}


    AND repairReason = #{repairReason}


    AND handleStatus = #{handleStatus}


    AND handleStatusName = #{handleStatusName}


    AND handleDate = #{handleDate}


    AND handleReason = #{handleReason}

mapper.update:
update EqmRepairRecord
handleReason = #{handleReason}, handleStatus=#{handleStatus}, HandleStatusName=#{handleStatusName}, HandleDate=now()
where keyID = #{keyID}
